import React from 'react';
import { Formik, Field, Form, ErrorMessage } from 'formik';
import { useDispatch } from 'react-redux';
import { addUsers } from '../actions';
import * as Yup from 'yup';
import store from "../store"
import { checkAccount, checkAge, checkPassword,  } from './validation';

export const SignupForm = ({}) => {
  const dispatch = useDispatch();
  return (
    <Formik
      initialValues={{ accountName: '', displayName: '', 
                       email: '', phone: '', dob: '', zipcode: '', password1: '', password2: ''}}
      validationSchema={Yup.object({
        accountName: Yup.string()
          .matches(/^(?=.*?[a-zA-Z])[a-zA-Z\d]+$/, "Account name can only contain letters and numbers")
          .required('Required'),
        displayName: Yup.string()
          .required('Required'),
        email: Yup.string().email('Invalid email address').required('Required'),
        phone: Yup.string().matches(/[0-9]{3}-[0-9]{3}-[0-9]{4}/, "Phone number is format 000-000-0000")
      })}
      onSubmit={(values, actions) => {
        dispatch(addUsers(values));
        actions.setSubmitting(false);
        actions.resetForm();
        console.log(store.getState());
      }}
    >
      <Form className="flex flex-col gap-2 w-40">
        <div className="mb-2 block">
          <label htmlFor="accountName" className="block text-sm font-medium text-gray-700">Account Name</label>
          <Field name="accountName" type="text" className="mt-1"/>
          <ErrorMessage name="accounttName" />
        </div>

        <div className="mb-2 block">
          <label htmlFor="displayName">Display Name</label>
          <Field name="displayName" type="text" />
          <ErrorMessage name="displayName" />
        </div>

        <div className="mb-2 block">
          <label htmlFor="email">Email Address</label>
          <Field name="email" type="email" />
          <ErrorMessage name="email" />
        </div>

        <div className="mb-2 block">
          <label htmlFor="phone">Phone Number</label>
          <Field name="phone" type="tel" />
          <ErrorMessage name="phone" />
        </div>

        <div className="mb-2 block">
          <label htmlFor="dob">Date of Birth</label>
          <Field name="dob" type="date" />
          <ErrorMessage name="dob" />
        </div>

        <div className="mb-2 block">
          <label htmlFor="zipcode">Zipcode</label>
          <Field name="zipcode" type="text" />
          <ErrorMessage name="zipcode" />
        </div>

        <div className="mb-2 block">
          <label htmlFor="password1">Password</label>
          <Field name="password1" type="password" />
          <ErrorMessage name="password" />
        </div>

        <div className="mb-2 block">
          <label htmlFor="password2">Confirm Password</label>
          <Field name="password2" type="password" />
          <ErrorMessage name="password" />
        </div>

        <button type="submit" className="inline-flex items-center rounded-md border border-transparent bg-indigo-600 px-3 py-2 text-sm font-medium leading-4 text-white shadow-sm hover:bg-indigo-700 focus:outline-none focus:ring-2 focus:ring-indigo-500 focus:ring-offset-2">
          Submit
        </button>
      </Form>
    </Formik>
  );
};
