import { Button } from 'flowbite-react'
import React from 'react'
import { useState } from 'react';

const Post = ({post, user, time}) => {
  const [buttonComment, setButton] = useState('Show comment'); 
  const [isShown, setIsShown] = useState(false);

  const handleClick = event => {
    setIsShown(current => !current);
    if (isShown) {
      setButton('Show comment');
    } else {
      setButton('Hide comment');
    }
  }

  return (
    <div className='bg-sky-100 rounded-lg m-3 p-4'>
      <img src="https://images.freeimages.com/images/large-previews/8a5/shooting-the-sunset-1381775.jpg" className='rounded w-20 my-3'/>
      <div>{time}</div>
      <div className='font-bold text-lg italic'>Author: {user}</div>
      <div className='font-bold text-lg'>{post.title}</div>
      <div>{post.body}</div>
      <button onClick={handleClick} className="py-2 my-2 items-center rounded-md border border-transparent bg-indigo-600 px-3 py-2 text-sm font-medium text-white shadow-sm hover:bg-indigo-700 focus:outline-none focus:ring-2 focus:ring-indigo-500 focus:ring-offset-2">
        {buttonComment}
      </button>
      <button className="py-2 my-2 mx-2 items-center rounded-md border border-transparent bg-indigo-600 px-3 py-2 text-sm font-medium text-white shadow-sm hover:bg-indigo-700 focus:outline-none focus:ring-2 focus:ring-indigo-500 focus:ring-offset-2">
        Comment
      </button>
      <button className="py-2 items-center rounded-md border border-transparent bg-indigo-600 px-3 py-2 text-sm font-medium text-white shadow-sm hover:bg-indigo-700 focus:outline-none focus:ring-2 focus:ring-indigo-500 focus:ring-offset-2">
        Edit
      </button>
      {isShown && (
        <div className='bg-indigo-400 rounded-md p-5'>
          <div>Bret: This is so cool!</div>
          <div>Anote: Awesome!</div>
        </div>
      )}
    </div>
  )
}

export default Post
