import { Formik, Field, Form, ErrorMessage } from 'formik'
import React, { Component } from 'react'
import { connect } from 'react-redux'
import * as Yup from 'yup';
import { addPosts } from '../../actions';

export class AddPost extends Component {
  handleSubmit =(values, action) => {
    values['userId'] = this.props.userID;
  }

  render() {
    return (
        <Formik
            initialValues={{title: '', body: ''}}
            validationSchema={Yup.object({
                title:Yup.string().required("Required"),
                body: Yup.string().required("Required")
            })}
            onSubmit={this.handleSubmit}
        >
            <Form>
                <div className="flex flex-col mb-2 block px-5 py-2">
                    <Field name="title" type="text" className="mt-1" placeholder="Add Your Title Here"/>
                    <ErrorMessage name="title" />
                </div>
                <div className="flex flex-col mb-2 block px-5 py-2">
                    <Field name="body" type="text" className="mt-1" placeholder="Add Your Contents Here"/>
                    <ErrorMessage name="body" />
                </div>
                <Field name="image" type="file" className="mt-1"></Field>
                <button type="submit" className="py-2 mx-4 items-center rounded-md border border-transparent bg-indigo-600 px-3 py-2 text-sm font-medium text-white shadow-sm hover:bg-indigo-700 focus:outline-none focus:ring-2 focus:ring-indigo-500 focus:ring-offset-2">
                Add Post
                </button>
                <button type="reset" className="py-2 mx-4 items-center rounded-md border border-transparent bg-indigo-600 px-3 py-2 text-sm font-medium text-white shadow-sm hover:bg-indigo-700 focus:outline-none focus:ring-2 focus:ring-indigo-500 focus:ring-offset-2">
                Cancel
                </button>
            </Form>    
        </Formik>
    )
  }
}

const mapStateToProps = (state) => {
    return {
        userID: state.riceZoneApp.currentUserID
    }
}

const mapDispatchToProps = (dispatch) => {
    return {
        addPost: (post) => dispatch(addPosts(post))
    }
};

export default connect(mapStateToProps, mapDispatchToProps)(AddPost)
