import React from 'react'
import { getPosts } from '../../actions';
import { connect } from 'react-redux'
import Post from './Post';
import Search from './search';

export class Posts extends React.Component {
    render() {
        return (
            <div className='bg-sky-100 rounded-lg p-10 m-3 grid-cols-2'>
                <Search/>
                {this.props.postList.map((post, index) => (
                    <Post key={index} post = {post} user = {this.props.user.username} time={this.props.time}/>
                ))}
            </div>  
          )
    }
}

const mapStateToProps = (state) => {
    return {
        postList: state.riceZoneApp.displayPost,
        user: state.riceZoneApp.currentUser,
        time: state.riceZoneApp.timeStamp
    }
};

const mapDispatchToProps = (dispatch) => {
    return {
        getPost: (user) => dispatch(getPosts(user))
    }
};

export default connect(mapStateToProps, mapDispatchToProps)(Posts)
