import React from 'react'
import { getPosts } from '../../actions';
import { connect } from 'react-redux'
import Post from './Post';
import Search from './search';

export class Posts extends React.Component {
    render() {
        return (
            <div className='rounded-lg p-10 m-3 flex flex-col items-center'>
                <Search/>
                <div className='grid grid-cols-2'>
                    {this.props.postList.map((post, index) => (
                        <Post key={index} post = {post} user = {post.author} time={this.props.time}/>
                    ))}
                </div>
            </div>  
          )
    }
}

const mapStateToProps = (state) => {
    return {
        postList: state.displayPost,
        user: state.currentUser,
        time: state.timeStamp
    }
};

const mapDispatchToProps = (dispatch) => {
    return {
        getPost: (user) => dispatch(getPosts(user))
    }
};

export default connect(mapStateToProps, mapDispatchToProps)(Posts)
