import * as Yup from 'yup';

let date = new Date(document.querySelector('#bod').value);
const date18YearsAgo = new Date();
date18YearsAgo.setFullYear(date18YearsAgo.getFullYear() - 18);

export const validation = Yup.object({
    accountName: Yup.string()
      .matches(/^(?=.*?[a-zA-Z])[a-zA-Z\d]+$/, "Account name can only contain letters and numbers")
      .required('Required'),
    displayName: Yup.string()
      .required('Required'),
    email: Yup.string().email('Invalid email address').required('Required'),
    phone: Yup.string().matches(/[0-9]{3}-[0-9]{3}-[0-9]{4}/, "Phone number is format 000-000-0000").required('Required'),
    dob: Yup.date().min()
  })
