import React, { Component } from 'react'
import store from '../../store'
import Posts from './Posts';
import { getPosts } from '../../actions';
import { connect } from 'react-redux'
import NavbarSection from './Navbar_main';
import AddPost from './AddPost';
import { fetchFollowers } from '../../actions';
import FollowedUserList from './FollowedUserList';
import UserStatus from './UserStatus';


export class MainPage extends Component {
  constructor(props){
    super(props);
    this.props.fetchFollowers(this.props.user.id);
    this.props.getPost(this.props.user.id);
    this.state = {userHeadline: ''};
    this.filterHeadline();
  }

  filterHeadline() {
    let headlineList = this.props.headline;
    console.log(headlineList)
    this.props.headline.forEach(user => {
      if (user.userId === this.props.user.id) {
        this.state.userHeadline = user.headline;
      } 
    });
  }

  render() {
    return (
      <>
      <NavbarSection/>
      <div className='flex'>
        <div className='flex flex-col'>
          <UserStatus headline={this.state.userHeadline}/>
          <FollowedUserList/>
        </div>
        <div>
          <AddPost/ >
          <Posts />
        </div>
      </div>
      </>
    )
  }
}

const mapStateToProps = (state) => {
  return {
      user: state.currentUser,
      headline: state.riceZoneApp.userHeadline
  }
};

const mapDispatchToProps = (dispatch) => {
  return {
      getPost: (user) => dispatch(getPosts(user)),
      fetchFollowers: (user) => dispatch(fetchFollowers(user))
  }
};

export default connect(mapStateToProps, mapDispatchToProps)(MainPage)
