import React from 'react';
import { Formik, Field, Form, ErrorMessage } from 'formik';
import * as Yup from 'yup';

export const LogInForm = ({}) => {
  return (
    <Formik
        initialValues={{ accountName: '', password: ''}}
        validationSchema={Yup.object({
            accountName: Yup.string().required("Required"),
            password: Yup.string().required("Password required")
        })}
        onSubmit={(values, actions) => {
            alert("Test");
        }}
    >
        <Form>
            <div className="mb-2 block">
                <div className="flex flex-col mb-2 block px-5 py-2">
                    <label htmlFor="accountName">Account Name</label>
                    <Field name="accountName" type="text" className="mt-1"/>
                    <ErrorMessage name="accountName" />
                    </div>
                <div className='flex flex-col mb-2 block px-5 py-2'> 
                    <label htmlFor="password">Password</label>
                    <Field name="password" type="password" />
                    <ErrorMessage name="password" />
                </div>

                <button type="submit" className="items-center rounded-md border border-transparent bg-indigo-600 px-3 py-2 text-sm font-medium text-white shadow-sm hover:bg-indigo-700 focus:outline-none focus:ring-2 focus:ring-indigo-500 focus:ring-offset-2">
                Log In
                </button>
            </div>  

        </Form>
    </Formik>
  )
};
