import { createReducer } from "@reduxjs/toolkit"
import { loadUsers, loadPosts } from "./actions"

const initialState = {
    userList: [],
    userValidated: false,
    postList: [],
    currentPost: [],
    userNum: 0,
    currentUser: ''
}

const riceZoneApp = createReducer([], (builder) => {
