            let userList = [...state.userList];
            let payload = action.payload;
            let num = state.userNum;
            num++;
            payload['id'] = state.userNum + 1;
            userList.push(payload);
            return {
                ...state,
                userList: userList,
                userNum: num,
                currentUser: num
            }
        })
        .addCase('usersValidated', (state, action) => {
            let logIn = action.payload;
            state.userList.forEach(user => {
                if (user.username === logIn.username) {
                    if (user.password1) {
                        if (user.password1 == logIn.password) {
                            state.userValidated = true;
                            state.currentUser = user.id; 
                        }
                    } else {
                        if (user.address.street == logIn.password) {
                            state.userValidated = true;
                            state.currentUser = user.id; 
                        }
                    }

                }
            });
        })
        .addCase('resetValidate', (state, action) => {
            state.userValidated = false;
        })
        .addCase('postsLoaded', (state, action) => {
            return {
                ...state,
                postList: [...action.payload]
            }
        })
        .addCase('getPosts', (state, action) => {
            let posts = [...state.postList]
            let currentPosts = 
            posts.forEach(post => {
                if (post.userId === action.payload) {
                    posts.push(post);
                }
            });
            console.log(state.posts);
            return {
                ...state,
                currentPost: [...posts]
            }
        })
        .addDefaultCase((state, action) => {
            return state;
        })
})

export async function fetchUsers(dispatch, getState) {
    const response = await fetch("https://jsonplaceholder.typicode.com/users");
    const data = await response.json();
    dispatch(loadUsers(data));
}

export async function fetchPosts(dispatch, getState) {
    const response = await fetch("https://jsonplaceholder.typicode.com/posts");
    const data = await response.json();
    dispatch(loadPosts(data));
}

export default {riceZoneApp} ;
