import React, { useEffect } from 'react';
import { Formik, Field, Form, ErrorMessage } from 'formik';
import * as Yup from 'yup';
import { useSelector, useDispatch, shallowEqual } from 'react-redux';
import { Navigate, useNavigate } from 'react-router-dom';
import { validateUsers, resetValidate } from '../actions';
import toast, { Toaster } from 'react-hot-toast';

function ValidationPassword(userList, logIn) {
    let result = false;
    userList.forEach(user => {
        if (user.username === logIn.username) {
            if (user.password1) {
                if (user.password1 == logIn.password) {
                    result = true;
                }
            } else {
                if (user.address.street == logIn.password) {
                    result = true;
                }
            }

        }
    });
    return result;
}


export const LogInForm = ({}) => {
  const dispatch = useDispatch();
  const userList = useSelector(state => state.riceZoneApp.userList)
  const navigate = useNavigate();
  return (
    <Formik
        initialValues={{ username: '', password: ''}}
        validationSchema={Yup.object({
            username: Yup.string().required("Required"),
            password: Yup.string().required("Password required")
        })}
        onSubmit={(values, actions) => {
            dispatch(validateUsers(values));
            if (ValidationPassword(userList, values)) {
                navigate("/main");
            } else {
                toast.error('Log In Information incorrect.');
            }
            actions.setSubmitting(false);
            actions.resetForm();
        }}
    >
        <Form>
            <div className="flex flex-col mb-2 block">
                <div className="flex flex-col mb-2 block px-5 py-2">
                    <label htmlFor="username">Account Name</label>
                    <Field name="username" type="text" className="mt-1"/>
                    <ErrorMessage name="username" />
                </div>
                <div className='flex flex-col mb-2 block px-5 py-2'> 
                    <label htmlFor="password">Password</label>
                    <Field name="password" type="password" />
                    <ErrorMessage name="password" />
                </div>

                <button type="submit" className="py-2 mx-4 items-center rounded-md border border-transparent bg-indigo-600 px-3 py-2 text-sm font-medium text-white shadow-sm hover:bg-indigo-700 focus:outline-none focus:ring-2 focus:ring-indigo-500 focus:ring-offset-2">
                Log In
                </button>
            </div>       
        </Form>
    </Formik>
  )
};
