import React, { Component } from 'react'
import { connect } from 'react-redux'
import { removeFollowers } from '../../actions';
import AddFollower from './AddFollower';
import FollowedUser from './FollowedUser';

export class FollowedUserList extends React.Component {
  handleRemove = (username) => {
    this.props.removeFollower(username);
  }

  render() {
    return (
        <div className='bg-sky-100 rounded-lg p-10 m-3'>
          <AddFollower />
            {this.props.followerList.map((username, index) => (
                <FollowedUser key={index} username = {username} headline = {this.props.headline} handleRemove = {this.handleRemove}/>
            ))}
        </div>

    )
  }
}

const mapStateToProps = (state) => {
    return {
        followerList: state.riceZoneApp.followerList,
        headline: state.riceZoneApp.defaultHeadLine
    }
  };

const mapDispatchToProps = (dispatch) => {
    return {
        removeFollower: (user) => dispatch(removeFollowers(user))
    }
  };

export default connect(mapStateToProps, mapDispatchToProps)(FollowedUserList)
