import { createReducer } from "@reduxjs/toolkit"
import { loadUsers } from "./actions"

const initialState = {
    userList: [],
    userValidated: false
}

const riceZoneApp = createReducer([], (builder) => {
    builder
        .addCase('usersLoaded', (state, action) => {
            return {
                ...state,
                userList: [...action.payload],
                userValidated: false
            }
        })
        .addCase('usersAdded', (state, action) => {
            let userList = [...state.userList];
            userList.push(action.payload);
            return {
                ...state,
                userList: userList
            }
        })
        .addCase('usersValidated', (state, action) => {
            let logIn = action.payload;
            let result = false;
            state.userList.forEach(user => {
                if (user.username === logIn.username) {
                    if (user.password1) {
                        if (user.password1 == logIn.password) {
                            result = true;
                        }
                    } else {
                        if (user.address.street == logIn.password) {
                            result = true;
                        }
                    }

                }
            });
            return {
                ...state,
                userValidated: result
            }
        })
        .addCase('resetValidate', (state, action) => {
            let result = !state.userValidated;
            console.log(result)
            return {
                ...state,
                userValidated: result
            }
        })
        .addDefaultCase((state, action) => {
            return state;
        })
})

export async function fetchUsers(dispatch, getState) {
    const response = await fetch("https://jsonplaceholder.typicode.com/users");
    const data = await response.json();
    dispatch(loadUsers(data));
}

export default {riceZoneApp} ;
