import React, { Component } from 'react'
import { connect } from 'react-redux'
import { validation } from '../validation';
import { Formik, Field, Form, ErrorMessage } from 'formik';
import { updateProfile } from '../../actions';

const validate = values => {
  let errors;
  // if none empty and has correct format
  if (values.email && !/(^$|[^\s@]+@[^\s@]+\.[^\s@]+)/i.test(values.email)) {
      errors = 'Invalid email address'
  }

  if (values.zipcode && !/(^$|^\d{5})/i.test(values.zipcode)) {
      errors = 'Zipcode should be 5 digits'
  }

  if (values.password1 && values.password2 &&
      values.password !== values.password2) {
      errors = 'Passwords do not match'
  }

  return errors
}

export class UpdateCurrentInfo extends Component {
  handleSubmit = (values, actions) => {
    this.props.updateProfile(values);
    actions.setSubmitting(false);
    actions.resetForm();
  }

  render() {
    return (
        <Formik
        initialValues={{ username: '',  email: '', phone: '', zipcode: '', password1: '', password2: ''}}
        onSubmit={this.handleSubmit}
      >
        <Form className="flex flex-col gap-2 w-100">
          <div className="flex flex-col mb-2 block px-5">
            <label htmlFor="username">User Name</label>
            <Field name="username" type="text" className="mt-1"/>
            <ErrorMessage name="username" />
          </div>

          <div className="flex flex-col mb-2 block px-5">
            <label htmlFor="email">Email Address</label>
            <Field name="email" type="email" />
            <ErrorMessage name="email" />
          </div>

          <div className="flex flex-col mb-2 block px-5">
            <label htmlFor="phone">Phone Number</label>
            <Field name="phone" type="tel" />
            <ErrorMessage name="phone" />
          </div>

          <div className="flex flex-col mb-2 block px-5">
            <label htmlFor="zipcode">Zipcode</label>
            <Field name="zipcode" type="text" />
            <ErrorMessage name="zipcode" />
          </div>

          <div className='flex flex-col mb-2 block px-5'> 
            <label htmlFor="password1">Password</label>
            <Field name="password1" type="password" />
            <ErrorMessage name="password1" />
          </div>

          <div className="flex flex-col mb-2 block px-5">
            <label htmlFor="password2">Confirm Password</label>
            <Field name="password2" type="password" />
            <ErrorMessage name="password2" />
          </div>


          <button type="submit" className="w-50 items-center rounded-md border border-transparent bg-indigo-600 px-3 py-2 text-sm font-medium leading-4 text-white shadow-sm hover:bg-indigo-700 focus:outline-none focus:ring-2 focus:ring-indigo-500 focus:ring-offset-2">
            Update
          </button>
          </Form>
    </Formik>
    )
  }
}

const mapStateToProps = (state) => ({})

const mapDispatchToProps = (dispatch) => {
    return {
        updateProfile: (profile) => dispatch(updateProfile(profile))
    }
};

export default connect(mapStateToProps, mapDispatchToProps)(UpdateCurrentInfo)
