import { Button } from 'flowbite-react'
import React from 'react'

const FollowedUser = ({username, headline, handleRemove}) => {
  return (
    <div className='m-4'>
        <img src="https://mir-s3-cdn-cf.behance.net/project_modules/disp/8394f798931623.5ee79b6a909ea.jpg" className='rounded w-20'/>
        <div>{username}</div>
        <div>{headline}</div>
        <Button onClick={() => handleRemove({username})}>Remove</Button>
    </div>

  )
}

export default FollowedUser
