import React, { useState, useEffect } from "react";
import Posts from "./Posts";
import Pagination from "./Pagination";
import { connect } from 'react-redux'

export const Pagination_posts = (props) => {
  const [currentPage, setCurrentPage] = useState(1);
  const [postsPerPage] = useState(10);

  const indexOfLastPost = currentPage * postsPerPage;
  const indexOfFirstPost = indexOfLastPost - postsPerPage;
  const currentPosts = this.props.posts.slice(indexOfFirstPost, indexOfLastPost);

  const paginate = (pageNumber) => setCurrentPage(pageNumber);
  return (
    <div>
        <Posts posts={currentPosts} />
        <Pagination
            postsPerPage={postsPerPage}
            totalPosts={this.props.posts.length}
            paginate={paginate}
            currentPage={currentPage}
        />
    </div>
  )
}

const mapStateToProps = (state) => ({
    posts: state.postList
})

const mapDispatchToProps = {}

export default connect(mapStateToProps, mapDispatchToProps)(Pagination_posts)
