import React, { Component } from 'react'
import { connect } from 'react-redux'
import UpdateHeadline from './UpdateHeadline';

export class UserStatus extends Component {
  constructor(props){
    super(props);
    this.state = {headline: ''};
    this.props.headline.forEach(user => {
        if (user.userId === this.props.user.id) {
          this.state.headline = user.headline;
        } 
    });
  }
  render() {
    return (
      <div className='bg-sky-100 rounded-lg p-10 m-3 flex flex-col'>
        <div className='flex flex-col items-center '>
          <img src="https://mir-s3-cdn-cf.behance.net/project_modules/disp/8394f798931623.5ee79b6a909ea.jpg" className='rounded w-20'/>
          <div>{this.props.user.username}</div>
          <div className='italic font-bold'>{this.props.headline}</div>
        </div>
        <UpdateHeadline/>
      </div>
    )
  }
}

const mapStateToProps = (state) => {
    return {
        user: state.currentUser
    }
  };

const mapDispatchToProps = {}

export default connect(mapStateToProps, mapDispatchToProps)(UserStatus)
