import { createReducer, current } from "@reduxjs/toolkit"
import storage from 'redux-persist/lib/storage';
import { loadUsers, loadPosts, USERS_LOADED, USERS_ADDED, VALIDATE_USERS, RESET_VALIDATE, POSTS_LOADED, GET_POSTS, FITER_POSTS, ADD_POSTS, FETCH_FOLLOWERS, REMOVE_FOLLOWERS, ADD_FOLLOWERS, UPDATE_HEADLINE, UPDATE_PROFILE, GET_PROFILE, LOG_OUT, addUsers, validateUsers, resetValidate, GET_HEADLINE, getHeadline, updateHeadline, fetchFollowers, addFollowers } from "./actions"


const url = path => `http://localhost:3001${path}`;
const initialState = {
    userList: [],
    userExisted: false,
    userValidated: false,
    postList: [],
    currentPost: [],
    displayPost: [],
    postNum: 0,
    userNum: 0,
    currentUser: '',
    profile_username: '',
    timeStamp: '',
    defaultHeadLine: 'hello world',
    userHeadline: "",
    followerList: [],
    followerAddError: false,
    toggle: false
}

export function riceZoneApp( state = initialState, action) {
    switch (action.type) {
        //load placehoder users
        case USERS_LOADED:
            let date = new Date();
            let userList_placehoder = []
            action.payload.forEach(user => {
                user['password1'] = user.address.street
                user['zipcode'] = user.address.zipcode
                userList_placehoder.push(user)
            });
            return {
                ...state,
                userNum: action.payload.length,
                userList: [...userList_placehoder],
                timeStamp: date.toLocaleString(),
                defaultHeadLine: 'hello world'
            }
        case USERS_ADDED:
            let payload = action.payload;
            let userExists = false;
            let userValidated = false;
            let currentUser_register = ''; 
            if (payload.result === "User already exist") {
                userExists = true;
            } else {
                userValidated = true;
                currentUser_register = payload.username;
            }
            return {
                ...state,
                userExisted: userExists,
                userValidated: userValidated,
                currentUser: currentUser_register,
            }
        case VALIDATE_USERS:
            let logIn = action.payload;
            let currentUser = logIn.username;
            return {
                ...state,
                userValidated: true,
                currentUser: currentUser
            }
        case RESET_VALIDATE:
            let toggle = !state.toggle;
            return {
                ...state,
                toggle: toggle
            }       
        case POSTS_LOADED:
            return {
                ...state,
                postList: [...action.payload],
                postNum: action.payload.length
            }  
        case GET_POSTS:
            let posts = [...state.postList];
            let currentPosts = [];
            posts.forEach(post => {
                if (post.userId === action.payload.id) {
                    post['author'] = action.payload.username;
                    currentPosts.push(post);
                }
                state.followerList.forEach(follower => {
                    if (post.userId === follower.id) {
                        post['author'] = follower.username;
                        currentPosts.unshift(post);
                    }
                })
            });
            return {
                ...state,
                currentPost: [...currentPosts],
                displayPost: [...currentPosts]
            }
        case FITER_POSTS:
            let filterPosts = [...state.currentPost];
            let query = action.payload;
            let displayPosts = [];
            if (query === '') {
                displayPosts = filterPosts;
            } else {
                filterPosts.forEach(post => {
                    if (post.title.toLowerCase().includes(query.toLowerCase()) || post.body.toLowerCase().includes(query.toLowerCase())) {
                        displayPosts.push(post);
                    }
                })  
            }
            return {
                ...state,
                displayPost: displayPosts
            }
        case ADD_POSTS:
            let originalPosts = [...state.currentPost];
            let postToBeAdd = action.payload;
            let currentPostNum = state.postNum;
            postToBeAdd['author'] = state.currentUser.username;
            currentPostNum += 1;
            postToBeAdd['id'] = currentPostNum;
            originalPosts.unshift(postToBeAdd);
            return {
                ...state,
                postNum: currentPostNum,
                currentPost: originalPosts,
                displayPost: originalPosts
            }  
        case FETCH_FOLLOWERS:
            let follower = [...action.payload.following]
            return {
                ...state,
                followerList: [...follower]
            } 
        case REMOVE_FOLLOWERS:
            let currentFollower = [...state.followerList]
            let filteredFollower = currentFollower.filter((follower) => {
                return follower.username !== action.payload.username;
            });
            return {
                ...state,
                followerList: [...filteredFollower]
            }  
        case ADD_FOLLOWERS:
            let toggle_follower;
            let followerAddError = state.followerAddError;
            let follower_add = [...state.followerList]
            if (action.payload.following === "user not found" || action.payload.following === "user already followed") {
                followerAddError = true;
                toggle_follower = !state.toggle;
            } else {
                follower_add = [...action.payload.following]
                followerAddError = false;
            }
             return {
                ...state,
                followerList: follower_add,
                followerAddError: followerAddError,
                toggle: toggle_follower
             }
        case GET_HEADLINE:
             return {
                ...state,
                userHeadline: action.payload.headline
             }
        case UPDATE_HEADLINE:
            return {
                ...state,
                userHeadline: action.payload.headline
            } 
        case GET_PROFILE:
            return {
                ...state,
                profile_username: action.payload
            }   
        case UPDATE_PROFILE:
            let updatedUser = {}
            Object.assign(updatedUser, state.currentUser);
            if (action.payload.email !== '') {
                updatedUser.email = action.payload.email;
            }
            if (action.payload.phone !== '') {
                updatedUser.phone = action.payload.phone;
            }
            if (action.payload.zipcode !== '') {
                updatedUser.zipcode = action.payload.zipcode;
            }
            if (action.payload.password1 !== '') {
                updatedUser.password1 = action.payload.password;
            }
            return {
                ...state,
                currentUser: updatedUser
            }  
        case LOG_OUT:
            storage.removeItem('root');
            return initialState           
        default:
            return state;
    }
}

export async function fetchUsers(dispatch, getState) {
    const response = await fetch("https://jsonplaceholder.typicode.com/users");
    const data = await response.json();
    dispatch(loadUsers(data));
}

export async function fetchPosts(dispatch, getState) {
    const response = await fetch("https://jsonplaceholder.typicode.com/posts");
    const data = await response.json();
    dispatch(loadPosts(data));
}


export function createUser(payload) {
    return async function createUser(dispatch, getState) {
        const response = await fetch(url('/register'), {
            method: 'POST',
            headers: { 'Content-Type': 'application/json' },
            body: JSON.stringify(payload),
            credentials: "include"
        });
        const data = await response.json();
        dispatch(addUsers(data));
    }
}

export function logInUser(payload) {
    return async function logInUser(dispatch, getState) {
        const response = await fetch(url('/login'), {
            method: 'POST',
            headers: { 'Content-Type': 'application/json' },
            body: JSON.stringify(payload),
            credentials: "include"
        });
        if (response.status == 200) {
            const data = await response.json();
            dispatch(validateUsers(data));
        } else {
            dispatch(resetValidate());
        }
    }
}

export function fetchHeadline(payload) {
    return async function fetchHeadline(dispatch, getState) {
        let response;
        response = await fetch(url(`/headline/${payload.username}`), {
            method: 'GET',
            headers: { 'Content-Type': 'application/json'},
            credentials: "include"
        });
        if (response.status == 200) {
            const data = await response.json();
            dispatch(getHeadline(data));
        }
    }
}

export function updateHeadline_post(payload) {
    return async function updateHeadline_post(dispatch, getState) {
        let response;
        let body = {headline: payload.headline}
        response = await fetch(url(`/headline`), {
            method: 'PUT',
            headers: { 'Content-Type': 'application/json'},
            body: JSON.stringify(body),
            credentials: "include"
        });
        if (response.status == 200) {
            const data = await response.json();
            dispatch(updateHeadline(data));
        }
    }
}

export function fetchFollowers_get(payload) {
    return async function fetchFollowers_get(dispatch, getState) {
        const response = await fetch(url(`/following/${payload.username}`), {
            method: 'GET',
            headers: { 'Content-Type': 'application/json'},
            credentials: "include"
        });
        if (response.status == 200) {
            const data = await response.json();
            dispatch(fetchFollowers(data));
        }
    }
}

export function addFollowers_put(payload) {
    return async function addFollowers_put(dispatch, getState) {
        const response = await fetch(url(`/following/${payload.follower}`), {
            method: 'PUT',
            headers: { 'Content-Type': 'application/json'},
            credentials: "include"
        });
        if (response.status == 200) {
            const data = await response.json();
            dispatch(addFollowers(data));
        }
    }
}

export default {riceZoneApp} ;
