import { createReducer, current } from "@reduxjs/toolkit"
import { loadUsers, loadPosts, USERS_LOADED, USERS_ADDED, VALIDATE_USERS, RESET_VALIDATE, POSTS_LOADED, GET_POSTS, FITER_POSTS, ADD_POSTS, FETCH_FOLLOWERS, REMOVE_FOLLOWERS, ADD_FOLLOWERS, UPDATE_HEADLINE, UPDATE_PROFILE, GET_PROFILE, LOG_OUT } from "./actions"

const initialState = {
    userList: [],
    userValidated: false,
    postList: [],
    currentPost: [],
    displayPost: [],
    postNum: 0,
    userNum: 0,
    currentUser: '',
    profile_username: '',
    timeStamp: '',
    defaultHeadLine: 'hello world',
    userHeadlineList: [
        {'userId': 1, 'headline': 'hello world'},
        {'userId': 2, 'headline': 'hello world'},
        {'userId': 3, 'headline': 'hello world'},
        {'userId': 4, 'headline': 'hello world'},
        {'userId': 5, 'headline': 'hello world'},
        {'userId': 6, 'headline': 'hello world'},
        {'userId': 7, 'headline': 'hello world'},
        {'userId': 8, 'headline': 'hello world'},
        {'userId': 9, 'headline': 'hello world'},
        {'userId': 10, 'headline': 'hello world'}
    ],
    followerList: [],
    addFollowerSuccess: false,
    followerReference: [
        {'userId': 1, 'following': [2, 3, 4]},
        {'userId': 2, 'following': [3, 4, 5]},
        {'userId': 3, 'following': [4, 5, 6]},
        {'userId': 4, 'following': [5, 6, 7]},
        {'userId': 5, 'following': [6, 7, 8]},
        {'userId': 6, 'following': [7, 8, 9]},
        {'userId': 7, 'following': [8, 9, 10]},
        {'userId': 8, 'following': [9, 10, 1]},
        {'userId': 9, 'following': [10, 1, 2]},
        {'userId': 10, 'following': [1, 2, 3]}
    ]
}

export function riceZoneApp( state = initialState, action) {
    switch (action.type) {
        //load placehoder users
        case USERS_LOADED:
            let date = new Date();
            let userList_placehoder = []
            action.payload.forEach(user => {
                user['password1'] = user.address.street
                user['zipcode'] = user.address.zipcode
                userList_placehoder.push(user)
            });
            return {
                ...state,
                userNum: action.payload.length,
                userList: [...userList_placehoder],
                timeStamp: date.toLocaleString(),
                defaultHeadLine: 'hello world'
            }
        case USERS_ADDED:
            let userList = [...state.userList];
            let payload = action.payload;
            let headlineList = [...state.userHeadlineList];
            let num = state.userNum;
            num++;
            payload['id'] = state.userNum + 1;
            headlineList.push({'userId': num, 'headline': 'hello world'});
            userList.push(payload);
            return {
                ...state,
                userList: userList,
                userNum: num,
                currentUser: payload,
                userValidated: true,
                userHeadline: [...headlineList]
            }
        case VALIDATE_USERS:
            let logIn = action.payload;
            let result = false;
            let currentUser;
            state.userList.forEach(user => {
                if (user.username === logIn.username) {
                    if (user.password1 == logIn.password) {
                        result = true;
                        currentUser = user
                    }

                }
            });
            return {
                ...state,
                userValidated: result,
                currentUser: currentUser
            }
        case RESET_VALIDATE:
            return {
                ...state,
                usersValidated: false
            }       
        case POSTS_LOADED:
            return {
                ...state,
                postList: [...action.payload],
                postNum: action.payload.length
            }  
        case GET_POSTS:
            let posts = [...state.postList];
            let currentPosts = [];
            posts.forEach(post => {
                if (post.userId === action.payload.id) {
                    post['author'] = action.payload.username;
                    currentPosts.push(post);
                }
                state.followerList.forEach(follower => {
                    if (post.userId === follower.id) {
                        post['author'] = follower.username;
                        currentPosts.unshift(post);
                    }
                })
            });
            return {
                ...state,
                currentPost: [...currentPosts],
                displayPost: [...currentPosts]
            }
        case FITER_POSTS:
            let filterPosts = [...state.currentPost];
            let query = action.payload;
            let displayPosts = [];
            if (query === '') {
                displayPosts = filterPosts;
            } else {
                filterPosts.forEach(post => {
                    if (post.title.toLowerCase().includes(query.toLowerCase()) || post.body.toLowerCase().includes(query.toLowerCase())) {
                        displayPosts.push(post);
                    }
                })  
            }
            return {
                ...state,
                displayPost: displayPosts
            }
        case ADD_POSTS:
            let originalPosts = [...state.currentPost];
            let postToBeAdd = action.payload;
            let currentPostNum = state.postNum;
            postToBeAdd['author'] = state.currentUser.username;
            currentPostNum += 1;
            postToBeAdd['id'] = currentPostNum;
            originalPosts.unshift(postToBeAdd);
            return {
                ...state,
                postNum: currentPostNum,
                currentPost: originalPosts,
                displayPost: originalPosts
            }  
        case FETCH_FOLLOWERS:
            let followerData = [...state.followerReference];
            let follower = [];
            followerData.forEach(user => {
                if (user.userId === action.payload) {
                    follower = user.following.map((post, index) => {
                        let user_added = '';
                        state.userList.forEach(user => {
                            if (user.id === post) {
                                user_added = user;
                            }
                        })
                        return user_added;
                    })
                }
            });
            return {
                ...state,
                followerList: [...follower]
            } 
        case REMOVE_FOLLOWERS:
            let currentFollower = [...state.followerList]
            let filteredFollower = currentFollower.filter((follower) => {
                return follower.username !== action.payload.username;
            });
            return {
                ...state,
                followerList: [...filteredFollower]
            }  
        case ADD_FOLLOWERS:
             let currentFollowerList = [...state.followerList]
             let addFollowerSuccess = false;
             if (state.currentUser.username !== action.payload.follower) {
                state.userList.forEach(user => {
                    if (user.username === action.payload.follower) {
                        currentFollowerList.unshift(user);
                        addFollowerSuccess = true;
                    }
                 })
             }
             return {
                ...state,
                followerList: [...currentFollowerList],
                addFollowerSuccess: addFollowerSuccess
             }   
        case UPDATE_HEADLINE:
            let userheadline = [...state.userHeadlineList];
            userheadline.forEach(user => {
                if (user.userId === state.currentUser.id) {
                    user.headline = action.payload.headline;
                }
            });
            return {
                ...state,
                userHeadlineList: [...userheadline]
            } 
        case GET_PROFILE:
            return {
                ...state,
                profile_username: action.payload
            }   
        case UPDATE_PROFILE:
            let updatedUser = {}
            Object.assign(updatedUser, state.currentUser);
            if (action.payload.email !== '') {
                updatedUser.email = action.payload.email;
            }
            if (action.payload.phone !== '') {
                updatedUser.phone = action.payload.phone;
            }
            if (action.payload.zipcode !== '') {
                updatedUser.zipcode = action.payload.zipcode;
            }
            if (action.payload.password1 !== '') {
                updatedUser.password1 = action.payload.password;
            }
            return {
                ...state,
                currentUser: updatedUser
            }  
        case LOG_OUT:
            return {
                ...state,
                currentUser: '',
                userValidated: false
            }              
        default:
            return state;
    }
}

export async function fetchUsers(dispatch, getState) {
    const response = await fetch("https://jsonplaceholder.typicode.com/users");
    const data = await response.json();
    dispatch(loadUsers(data));
}

export async function fetchPosts(dispatch, getState) {
    const response = await fetch("https://jsonplaceholder.typicode.com/posts");
    const data = await response.json();
    dispatch(loadPosts(data));
}

export default {riceZoneApp} ;
