import React from 'react'
import { getPosts } from '../../actions';
import { connect } from 'react-redux'
import Post from './Post';
import { MagnifyingGlassIcon } from '@heroicons/react/20/solid'

export class Posts extends React.Component {
    render() {
        return (
            <>
                <div className="w-full sm:max-w-xs ml-20">
                    <label htmlFor="search" className="sr-only">
                            Search
                    </label>
                    <div className="relative">
                    <div className="pointer-events-none absolute inset-y-0 left-0 flex items-center pl-3">
                        <MagnifyingGlassIcon className="h-5 w-5 text-gray-400" aria-hidden="true" />
                    </div>
                        <input
                            id="search"
                            name="search"
                            className="block w-full rounded-md border border-transparent bg-gray-700 py-2 pl-10 pr-3 text-sm placeholder-gray-400 focus:border-white focus:bg-white focus:text-gray-900 focus:placeholder-gray-500 focus:outline-none focus:ring-white sm:text-sm"
                            placeholder="Search"
                            type="search"
                        />
                    </div>
                </div>
                {this.props.postList.map((post, index) => (
                    <Post key={index} post = {post} user = {this.props.user}/>
                ))}
            </>
          )
    }
}

const mapStateToProps = (state) => {
    return {
        postList: state.riceZoneApp.currentPost,
        user: state.riceZoneApp.currentUser
    }
};

const mapDispatchToProps = (dispatch) => {
    return {
        getPost: (user) => dispatch(getPosts(user))
    }
};

export default connect(mapStateToProps, mapDispatchToProps)(Posts)
