import React, { Component } from 'react'
import { connect } from 'react-redux'
import { Formik, Field, Form, ErrorMessage } from 'formik'
import * as Yup from 'yup';
import { addFollowers } from '../../actions';

export class AddFollower extends Component {
    handleSubmit =(values, actions) => {
        this.props.addFollower(values);
        actions.setSubmitting(false);
        actions.resetForm();
    }  
    render() {
        return (
            <Formik
                initialValues={{follower: ''}}
                validationSchema={Yup.object({
                    follower:Yup.string().required("Required")
                })}
                onSubmit={this.handleSubmit}
            >
                <Form>
                    <div className="flex flex-col mb-2 block px-5 py-2">
                        <Field name="follower" type="text" className="mt-1" placeholder="Add a follower"/>
                        <ErrorMessage name="follower" />
                    </div>
                    <button type="submit" className="py-2 mx-4 items-center rounded-md border border-transparent bg-indigo-600 px-3 py-2 text-sm font-medium text-white shadow-sm hover:bg-indigo-700 focus:outline-none focus:ring-2 focus:ring-indigo-500 focus:ring-offset-2">
                    Add User
                    </button>
                </Form>    
            </Formik>
        )
    }
}

const mapStateToProps = (state) => ({})

const mapDispatchToProps = (dispatch) => {
    return {
        addFollower: (user) => dispatch(addFollowers(user))
    }
};

export default connect(mapStateToProps, mapDispatchToProps)(AddFollower)
