export function checkAccount(value){
    //make sure first character is not digit
    if (!value) {
        error = 'Required';
    }
    if (/^\d/.test(account)){
        error = "First character of the account cannot be digit.";
    }

    //make sure only letters and nums can be accepted and that at least one letter is required
    if (!/^(?=.*?[a-zA-Z])[a-zA-Z\d]+$/.test(account)){
        error = alert("The account name may contain both letters and numbers or only letters.")
    } else {

        return false;
    }
}

export function checkAge(){
    const form = document.querySelector('form');
    let date = new Date(document.querySelector('#bod').value);
    const date18YearsAgo = new Date();
    date18YearsAgo.setFullYear(date18YearsAgo.getFullYear() - 18);

    if (date <= date18YearsAgo){
        return true;
    } else {
        alert("Age must be at least 18 years or older to register.");
        form.reset();
        return false;
    }
}

export function checkPassword(){
    const form = document.querySelector('form');
    const pass1 = document.querySelector('#password1').value;
    const pass2 = document.querySelector('#password2').value;
    if (pass1 !== pass2) {
        alert("Passwords do not match.");
        form.reset();
        return false;
    } else {
        return true;
    }
}

export function generateTimestamp(){
    const timeInMillis = Date.now();
    const timeStamp = document.querySelector('#timestamp');
    timeStamp.value = timeInMillis;
    return true;
}
